from customtkinter import *
from tkinter import PhotoImage
from time import sleep
import platform
import wget
import shutil

des = CTk()
des.geometry('800x500')
des.title('WALMFAST Installer')
des.resizable(False, False)

system = None

def select_language(language):
    global lang
    select_eng_language_button.place_forget()
    select_rus_language_button.place_forget()
    
    if language == 'rus':
        import rus as lang
    elif language == 'eng':
        import eng as lang

    hader.configure(text=lang.walmfast_installer)
    description.place(x=25, y=250)

    installer()

def installer():
    global system
    if platform.system() == 'Linux':
        print('System is Linux')
        system = 'Linux'
        description.configure(text=lang.installing_depen)
        des.update()
        sleep(2)
        
        if os.system('apt list') == 0:
            print('debian')
            os.system('pkexec apt install -y python3-pip python3-tk python3-pil.imagetk zenity')
        elif os.system('pacman -Q') == 0:
            print('arch')
            os.system('pkexec pacman -S python-pip tcl tk zenity --noconfirm')
        else:
            pass

        description.configure(text=lang.installing)
        description.place_forget()
        sleep(1)
        hader.configure(text=lang.branch)
        select_main_button.place(x=125, y=200)
        select_dev_button.place(x=300, y=200)
        des.update()
    elif platform.system() == 'Windows':
        print('System is Windows')
        system = 'Windows'
        description.configure(text=lang.installing_depen)
        des.update()
        sleep(2)

        wget.download('https://www.python.org/ftp/python/3.12.7/python-3.12.7-amd64.exe')
        os.system('start python-3.12.7-amd64.exe')

        description.configure(text=lang.installing)
        description.place_forget()
        sleep(1)
        hader.configure(text=lang.branch)
        select_main_button.place(x=125, y=200)
        select_dev_button.place(x=300, y=200)
        des.update()

def installing_branch(branch):
    hader.configure(text=lang.walmfast_installer)
    description.place(x=25, y=250)
    select_dev_button.place_forget()
    select_main_button.place_forget()
    des.update()
    wget.download(f'https://github.com/WALMFAST/walmfast/archive/refs/heads/{branch}.zip')
    shutil.unpack_archive(f'walmfast-{branch}.zip')
    os.remove(f'walmfast-{branch}.zip')
    try:
        os.remove('walmfast/Driver_Auto_Installer_EXE_v5.1632.00.zip')
    except:
        pass

    description.configure(text=lang.install_done)
    des.update()

background_base = r""

icon_base = ""

background = PhotoImage(data=background_base)
background_label = CTkLabel(des, image=background, text='').place(x=0,y=0)

icon = PhotoImage(data=icon_base)
des.iconphoto(False, icon)

hader = CTkLabel(des, text='Change Language/Выберите язык', font=('Arial', 35), bg_color='black', width=550, justify='center')
hader.place(x=25, y=100)

description = CTkLabel(des, text='', font=('Arial', 35), bg_color='black', width=550, justify='center')

select_dev_button = CTkButton(des, text='dev', font=('Arial', 35), text_color='white', bg_color='black', fg_color='black', border_color='white', corner_radius=2, hover_color='grey', border_width=2, command=lambda:installing_branch('dev'))
select_main_button = CTkButton(des, text='main', font=('Arial', 35), text_color='white', corner_radius=2, bg_color='black', fg_color='black', hover_color='grey', border_color='white',  border_width=2, command=lambda:installing_branch('main'))

select_rus_language_button = CTkButton(des, text='Русский', font=('Arial', 35), text_color='white', bg_color='black', fg_color='black', border_color='white', corner_radius=2, hover_color='grey', border_width=2, command=lambda:select_language('rus'))
select_eng_language_button = CTkButton(des, text='English', font=('Arial', 35), text_color='white', corner_radius=2, bg_color='black', fg_color='black', hover_color='grey', border_color='white',  border_width=2, command=lambda:select_language('eng'))
select_eng_language_button.place(x=125, y=200)
select_rus_language_button.place(x=300, y=200)
        
des.mainloop()